parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "interview": {
      "type": "object",
      "properties": {
        "title": {
          "type": "string",
          "description": "The title of the interview."
        },
        "participants": {
          "type": "object",
          "properties": {
            "owner": {
              "type": "object",
              "properties": {
                "name": {
                  "type": "string",
                  "description": "The name of the product owner."
                },
                "role": {
                  "type": "string",
                  "description": "The role of the product owner, typically 'Product Owner'."
                }
              },
              "required": ["name", "role"]
            },
            "interviewer": {
              "type": "object",
              "properties": {
                "name": {
                  "type": "string",
                  "description": "The name of the interviewer."
                },
                "role": {
                  "type": "string",
                  "description": "The role of the interviewer, typically 'Interviewer'."
                }
              },
              "required": ["name", "role"]
            }
          },
          "required": ["owner", "interviewer"]
        },
        "introduction": {
          "type": "string",
          "description": "The introduction text where the interviewer welcomes the product owner and introduces the product."
        },
        "questions_and_answers": {
          "type": "array",
          "description": "An array of objects representing the questions asked by the interviewer and the corresponding answers from the product owner.",
          "items": {
            "type": "object",
            "properties": {
              "question": {
                "type": "string",
                "description": "The question asked by the interviewer."
              },
              "answer": {
                "type": "string",
                "description": "The answer given by the product owner."
              }
            },
            "required": ["question", "answer"]
          }
        },
        "conclusion": {
          "type": "object",
          "properties": {
            "interviewer_summary": {
              "type": "string",
              "description": "The summary of the interview provided by the interviewer."
            },
            "owner_final_thoughts": {
              "type": "string",
              "description": "The final thoughts or call to action given by the product owner."
            }
          },
          "required": ["interviewer_summary", "owner_final_thoughts"]
        }
      },
      "required": ["title", "participants", "introduction", "questions_and_answers", "conclusion"]
    }
  },
  "required": ["interview"]
}

