{
  "$schema": "http://json-schema.org/draft-07/schema#",
  "title": "Analyse de l'entreprise",
  "type": "object",
  "properties": {
    "nom_entreprise": {
      "type": "string",
      "description": "Nom officiel de l'entreprise tel qu'il apparaît dans les documents."
    },
    "mission_entreprise": {
      "type": "string",
      "description": "Raison d'être, objectifs fondamentaux et vision de l'entreprise."
    },
    "valeurs_principales": {
      "type": "array",
      "items": {
        "type": "string"
      },
      "description": "Liste des valeurs qui guident l'entreprise (ex : innovation, excellence, responsabilité, etc.)."
    },
    "historique_contexte": {
      "type": "string",
      "description": "Résumé de l'évolution de l'entreprise, incluant ses débuts, ses étapes clés et son positionnement historique."
    },
    "produits_services": {
      "type": "string",
      "description": "Description des principaux produits ou services offerts par l'entreprise."
    },
    "site_web": {
      "type": "string",
      "format": "uri",
      "description": "URL du site web officiel de l'entreprise, le cas échéant."
    }
  },
  "required": [
    "nom_entreprise",
    "mission_entreprise",
    "valeurs_principales",
    "historique_contexte",
    "produits_services",
    "site_web"
  ]
}

