// "Filter Data" Code Node
// 1) Get *all* items from the Merge node
const allItems = $input.all();

// 2) Find the one item that holds `existingSet`
let existingSetArray = [];
const existingSetItem = allItems.find(item => Array.isArray(item.json.existingSet));
if (existingSetItem) {
  existingSetArray = existingSetItem.json.existingSet;
}
const existingSet = new Set(existingSetArray);

// 3) Prepare a set to track duplicates in this run and an output array
const seenLinksDuringThisRun = new Set();
const filteredOutput = [];

// 4) Loop through any items that have the Google search results
//    (they typically have `kind === 'customsearch#search'` in item.json)
for (const item of allItems) {
  if (item.json.kind === 'customsearch#search') {
    const searchResults = item.json.items || [];
    for (const result of searchResults) {
      const url = result.link?.trim().toLowerCase();
      if (!url) continue;

      // Skip if we've already seen it this run or it's in existingSet
      if (!seenLinksDuringThisRun.has(url) && !existingSet.has(url)) {
        seenLinksDuringThisRun.add(url);
        filteredOutput.push({
          json: {
            name: result.title,
            url: result.link,
          },
        });
      }
    }
  }
}

// 5) Return newly-found unique profiles
return filteredOutput;

// --- Get Data from Merged Input & Trigger ---

// Access the single merged item (contains OpenAI results & Count)
const mergedInput = $input.item.json;
console.log("Code Node Input (Merged):", JSON.stringify(mergedInput, null, 2)); // Log for debugging

// Access OpenAI results (VERIFY PATH from Merge1 output)
const openAIData = mergedInput.message?.content;
const jobTitle = openAIData?.jobTitle;
const location = openAIData?.location;
const industry = openAIData?.companyIndustry;

// Access the count (VERIFY PATH from Merge1 output)
const existingCount = mergedInput.existingCount;

// Access data DIRECTLY from the form trigger node
// !! REPLACE 'On form submission' with YOUR ACTUAL TRIGGER NODE NAME !!
const formTriggerNodeName = 'On form submission';
const formTriggerNode = $(formTriggerNodeName).first();

// Check if trigger node reference worked
if (!formTriggerNode || !formTriggerNode.json) {
  throw new Error(`Could not find valid data structure from trigger node named '${formTriggerNodeName}'. Check node name.`);
}
const formTriggerJson = formTriggerNode.json;

// Access specific fields using bracket notation for names with spaces/slashes
// !! VERIFY THESE FIELD NAMES EXACTLY from Form Trigger output !!
const profilesToFetchFieldName = 'Number of NEW Profiles to Find'; // <-- VERIFY THIS NAME
const queryIdentifierFieldName = 'Campaign Name / Identifier';   // <-- As provided by user

const profilesToFetch = formTriggerJson[profilesToFetchFieldName] || 50; // Default to 50
const queryIdentifier = formTriggerJson[queryIdentifierFieldName];

// --- Validate Inputs ---
if (existingCount === undefined) {
  throw new Error("`existingCount` is missing from merged input. Check Merge1 output/connections.");
}
// Check if data was successfully retrieved from the trigger
if (profilesToFetch === undefined || queryIdentifier === undefined) {
    throw new Error(`Form trigger data ('${profilesToFetchFieldName}' or '${queryIdentifierFieldName}') could not be accessed from node '${formTriggerNodeName}'. Check field names and trigger output.`);
}
if (!openAIData || !jobTitle || !location ) {
   throw new Error("OpenAI data (`message.content`, `jobTitle`, or `location`) is missing from merged input. Check Merge1 output/connections.");
}

// --- Calculations ---
const startIndex = Math.floor(existingCount / 10) * 10 + 1;
const pagesToFetch = Math.ceil(profilesToFetch / 10);

// --- Prepare Output ---
const outputData = {
  json: {
     startIndex: startIndex,
     pagesToFetch: pagesToFetch,
     jobTitle: jobTitle,
     location: location,
     industry: industry,
     queryIdentifier: queryIdentifier,
     _debug_existingCount: existingCount,
     _debug_profilesToFetch: profilesToFetch
   }
};

// Return the single item containing all parameters
return [outputData];

// Node: "Build Existing Set"
const allItems = $input.all(); // Get all input items (n8n structure)
const existingURLs = new Set(); // Initialize a Set for unique URLs

// Loop through each input item
for (const item of allItems) {
  // Access the JSON data within the item using item.json
  // Use the CORRECT key 'url' (lowercase)
  // Also, good practice to check if it's actually a string before trimming
  if (item.json.url && typeof item.json.url === 'string') {
    // Add the trimmed, lowercase URL to the Set
    existingURLs.add(item.json.url.trim().toLowerCase());
  } else {
    // Optional: Log if a URL is missing or not a string, for debugging
    // console.log(`Skipping item at row_number ${item.json.row_number}: URL missing or not a string.`);
  }
}

// Return a single item containing an array of the unique URLs
// Convert the Set back to an Array for the final JSON output
return [{
  json: {
    existingSet: Array.from(existingURLs), // Convert Set to Array
  }
}];

