// $input.all() ensures we get the array of all input items
const incomingItems = $input.all();

// Get the total number of items
const count = incomingItems.length;

// Create the output data structure: a single object containing the count
// The output needs to be wrapped in an array as n8n expects item(s)
const outputData = [{
  json: {
    existingCount: count
  }
}];

// Return the single item array
return outputData;
